function findNaughtyStep(original, modified) {
  original = original??""
  modified = modified??""
  let naughtyStep = ""
  let index = 0
  let char
  const originalLength = original.length
  const modifiedLength = modified.length
  const isMissing = modifiedLength < originalLength
  const isLengthless = !originalLength && modifiedLength 
  || originalLength && !modifiedLength

  if (original === modified) return naughtyStep

  if (isLengthless) {
    return isMissing ? original[0] : modified[0]
  }

  for (; index < originalLength; index++) {
    char = original.charAt(index)
      if (modified.indexOf(char, index) !== index) {
        naughtyStep = isMissing ? char : modified[index]
        break
      }

    if (index === originalLength - 1 && !isMissing) {
      naughtyStep = modified[index + 1]
      break
    }
  }

  return naughtyStep
}

function cyberReindeer (road, time) {
  let roadArray = road.split('')
  const closedGateType = '|'
  const openedGateType = '*'
  const roadType = '.'
  const sleighType = 'S'
  const result = []
  const gatesTime = 5
  let moveIntervalTime = 0
  let currentMoveTime =0
  let sleighPosition = 0
  let currentPositionType = roadType
  let endOfRoad = false

  const openGates = () => {
    let joinedRoad = roadArray.join('')
    joinedRoad = joinedRoad.replaceAll(closedGateType, openedGateType)
    roadArray = joinedRoad.split('')
  }

  while (moveIntervalTime < time) {
    const shouldMove = !endOfRoad
      && Boolean(result.length)
      && roadArray[sleighPosition + 1] !== closedGateType

    if (shouldMove) {
      roadArray[sleighPosition] = currentPositionType
      sleighPosition++
      currentPositionType = roadArray[sleighPosition]
      roadArray[sleighPosition] = sleighType

      if (sleighPosition === roadArray.length) {
        endOfRoad = true
      }
    }

    currentMoveTime++
    moveIntervalTime++

    result.push(roadArray.join(''))

    if (moveIntervalTime === gatesTime) {
      openGates()
    }
  }

  return result
}

function maxDistance(movements) {
  let reducedMap = {
   '>': 1,
    '<': -1,
    '*': -1
  }
  let increasedMap = {
   '>': 1,
    '<': -1,
    '*': 1
  }
  let reduced = 0
  let increased = 0
  let movement

  for (let i = 0; i < movements.length; i++) {
    movement = movements[i]
    reduced += reducedMap[movement]
    increased += increasedMap[movement]
  }

  return Math.max(Math.abs(reduced), Math.abs(increased))
}

function drawGift(size, symbol) {
  let giftLine = '#';
  let giftBorders = 2;
  let drawing = '';
  let tdBox = [];
  let tdBoxBottom = [];

  function drawTop() {
    let top = [];
    let bottom = [];
    let spaces = size - 1;
    let charsPerLine = size * 2 - 1;
    let halfChars
    let restChars
    let halfRestChars

    for (let i = 0; i < size; i++) {
      top = `${' '.repeat(spaces)}${giftLine}`.split('');

      if (i === 0) {
        halfChars = halfRestChars = giftLine
          .repeat(charsPerLine - top.length)
          .split('');
        bottom = halfChars.concat([giftLine])
      } else if (i === size - 1) {
        halfChars = giftLine.repeat(size - 1).split('');
        restChars = `${symbol.repeat(i - 1)}${giftLine}`.split('');
        halfRestChars = halfChars.concat(restChars)
        bottom.length = 0;
      } else if (i > 0 && i < size) {
        // fill
          halfChars = symbol.repeat(size - giftBorders).split('');
          halfChars.push(giftLine);
          restChars = `${symbol.repeat(i - 1)}${giftLine}`.split('');

          halfRestChars = halfChars.concat(restChars);
          bottom = `${giftLine}${halfChars
            .concat(restChars)
            .join('')}`
            .split('');
      }
      top = top.concat(halfRestChars);
      spaces--;

      top.push(`\n`);

      if (bottom.length) {
        bottom.push(`\n`);
        tdBoxBottom.unshift(...bottom);
      }

      tdBox.push(...top);
    }
  }

  if (size === 1) {
    drawing = [giftLine, '\n'];
  } else {
    drawTop();
    drawing = tdBox.concat(tdBoxBottom)
  }

  return `${drawing.join("")}`;
}
