function findFirstRepeated(gifts) {
  let firstRepeated = -1
  let repeatedPositions = []
  let currentGiftId
  let indexOfFirstRepeatedGiftId
  let nextNearestRepeatedIndex

  gifts = gifts ?? []

  for (let i = 0; i < gifts.length; i++) {
    currentGiftId = gifts[i]
    nextNearestRepeatedIndex = gifts.indexOf(currentGiftId, i + 1)

    if (nextNearestRepeatedIndex >= 0) {
      repeatedPositions.push(nextNearestRepeatedIndex)
    }
  }

  if (repeatedPositions.length > 0) {
    indexOfFirstRepeatedGiftId = Math.min.apply(Math, repeatedPositions)
    firstRepeated = gifts[indexOfFirstRepeatedGiftId]
  }

  return firstRepeated}
